function self:Refresh()
	-- Keep the invisman model always loaded, otherwise preview may randomly break if the model gets unloaded due to it not being used anywhere
	if ezCollections.CreatureWeaponPreview and ezCollections.CreatureWeaponPreview ~= 0 then
		self:Hide();
		self:Show();
		self:SetCreature(ezCollections.CreatureWeaponPreview);
	end
end

Mixin(self.TransmogStateTexture, SetAtlasMixin);
Mixin(self.Border, SetAtlasMixin);
Mixin(self, WardrobeItemsModelMixin);
self:SetScript("OnUpdate", self.OnUpdate);
self:OnLoad();
function self:SetSkin(skin)
	local model = self;
	local popup = self:GetParent():GetParent();

	if skin then
		local info = ezCollections:GetSkinInfo(skin);
		local isArmor = not (info and info.Weapon);
		local needsDressing = skin ~= model.skin or model.awaitingItemCache;
		local hasItemData = GetItemInfo(skin);
		if not hasItemData then
			ezCollections:QueryItem(skin);
		end

		local invType = select(9, GetItemInfo(skin));
		local isOffHand = invType == "INVTYPE_WEAPONOFFHAND" or invType == "INVTYPE_HOLDABLE" or invType == "INVTYPE_RANGED";
		if needsDressing and not isArmor then
			WardrobeCollectionFrame.DualWieldResetter:Dress();
		end
		model:SetModelScale(isArmor and 10 or 1);
		model:SetType(isArmor and "player" or isOffHand and "off" or "main");

		model:Show();

		local cameraID = C_TransmogCollection.GetAppearanceCameraID(skin);
		Model_ApplyUICamera(model, cameraID);
		model.cameraID = cameraID;

		if needsDressing then
			model:Undress();
			model:TryOn(skin);
		end
		model.skin = skin;

		if ( skin == popup.skin ) then
			model.TransmogStateTexture:SetAtlas("transmog-wardrobe-border-current", true);
			model.TransmogStateTexture:Show();
		else
			model.TransmogStateTexture:Hide();
		end

		model.Border:SetAtlas("transmog-wardrobe-border-collected");

		if GameTooltip:GetOwner() == model then
			GameTooltip:SetOwner(self, "ANCHOR_BOTTOM");
			GameTooltip:SetHyperlink("item:"..self.skin);
			GameTooltip:Show();
		end

		if hasItemData then
			model.Loading:Hide();
			model.awaitingItemCache = nil;
		else
			model:SetPosition(0, 100, 0);
			model.cameraID = nil;
			model.awaitingItemCache = skin;
			model.Loading:Show();
			model.Loading.AnimFrame.Anim:Play();
			if not popup.itemCacheRefreshTimer then
				popup.itemCacheRefreshTimer = ezCollections.AceAddon:ScheduleRepeatingTimer(function()
					local missing = false;
					if popup:IsVisible() then
						for _, model in ipairs(popup.Models) do
							if model:IsShown() and model.awaitingItemCache then
								if GetItemInfo(model.awaitingItemCache) then
									model:SetSkin(model.skin);
								else
									missing = true;
								end
							end
						end
					end
					if not missing then
						ezCollections.AceAddon:CancelTimer(popup.itemCacheRefreshTimer);
						popup.itemCacheRefreshTimer = nil;
					end
				end, 0.1);
			end
		end
	else
		model:Hide();
		model.type = nil;
		model.skin = nil;
		model.Loading:Hide();
		model.awaitingItemCache = nil;
	end
end

