function self.SetOption(model, option)
	local baseSourceID, baseVisualID, appliedSourceID, appliedVisualID, pendingSourceID, pendingVisualID, hasPendingUndo = C_Transmog.GetSlotVisualInfo(GetInventorySlotInfo(model.Slot), LE_TRANSMOG_TYPE_APPEARANCE);

	model:SetModelScale(10);
	model:SetType("player");
	model:Show();
	model:Undress();
	model:TryOn(appliedSourceID ~= 0 and appliedSourceID ~= ezCollections:GetHiddenVisualItem() and appliedSourceID or baseSourceID);

	local oldOption = ezCollections.Config.Wardrobe.CameraOption;
	ezCollections.Config.Wardrobe.CameraOption = option;
	local cameraID = ezCollections:GetCharacterCameraID(model.InvType);
	ezCollections.Config.Wardrobe.CameraOption = oldOption;
	Model_ApplyUICamera(model, cameraID);
	model.cameraID = cameraID;

	model.Border:SetAtlas("transmog-wardrobe-border-collected");
end

Scripts.OnClick:
PlaySound("igMainMenuOptionCheckBoxOn");
if self:GetParent().Option then
	ezCollections.Config.Wardrobe.CameraOption = self:GetParent().Option;
	ezCollections.Config.Wardrobe.CameraOptionSetup = true;
	ezCollections:RaiseEvent("TRANSMOG_COLLECTION_UPDATED");
end
StaticPopupSpecial_Hide(self:GetParent());

local prevOption = ezCollections.CameraOptions[#ezCollections.CameraOptions];
for index, option in ipairs(ezCollections.CameraOptions) do
	if option == self:GetParent().Option then
		break;
	else
		prevOption = option;
	end
end
self:GetParent():SetOption(prevOption);

local nextOption = ezCollections.CameraOptions[1];
for index, option in ipairs(ezCollections.CameraOptions) do
	if index > 1 and ezCollections.CameraOptions[index - 1] == self:GetParent().Option then
		nextOption = option;
		break;
	end
end
self:GetParent():SetOption(nextOption);

Scripts.OnLoad:
self.Option = nil;
self.Slots = { 1, 2, 4, 5, 3, 7, 8, 9, 10, 11 };
self.Models = { };
for i, slot in ipairs(self.Slots) do
	local slotInfo = TRANSMOG_SLOTS[slot];
	local name, _, _, _, _, _, invType = C_TransmogCollection.GetCategoryInfo(slotInfo.armorCategoryID);
	local model = _G["ezCollectionsCameraPreviewPopupModelsFrameModel"..i];
	model.Slot = slotInfo.slot;
	model.InvType = invType;
	model.Text:SetText(name);
	table.insert(self.Models, model);
end
self.exclusive = true;
self.hideOnEscape = true;

function self:SetOption(option)
	self.Option = option;
	for _, model in ipairs(self.Models) do
		model:SetOption(option);
	end
	UIDropDownMenu_SetSelectedValue(self.Options, option);
	UIDropDownMenu_SetText(self.Options, ezCollections:GetCameraOptionName(option));
end
UIDropDownMenu_SetWidth(self.Options, 160);
ezCollections:UIDropDownMenu_Initialize(self.Options, function()
	local info = UIDropDownMenu_CreateInfo();
	for index, option in ipairs(ezCollections.CameraOptions) do
		info.value = option;
		info.text = ezCollections:GetCameraOptionName(option);
		info.checked = self.Option == option;
		info.func = function(button)
			self:SetOption(button.value);
		end;
		UIDropDownMenu_AddButton(info);
	end
end);

function self:SetLeft(left)
	if left then
		SetClampedTextureRotation(self.Arrow.Arrow, 90);
		SetClampedTextureRotation(self.Arrow.Glow, 90);
		self.Arrow:ClearAllPoints();
		self.Arrow:SetPoint("RIGHT", self, "LEFT", 36, 16);
		self.Arrow.Glow:ClearAllPoints();
		self.Arrow.Glow:SetPoint("CENTER", self.Arrow.Arrow, "CENTER", -3, 0);
	else
		SetClampedTextureRotation(self.Arrow.Arrow, 270);
		SetClampedTextureRotation(self.Arrow.Glow, 270);
		self.Arrow:ClearAllPoints();
		self.Arrow:SetPoint("LEFT", self, "RIGHT", -2, 16);
		self.Arrow.Glow:ClearAllPoints();
		self.Arrow.Glow:SetPoint("CENTER", self.Arrow.Arrow, "CENTER", 3, 0);
	end
end
