    [43927] = { 24488, 1, 128, "Кенарийский боевой гиппогриф", "Члены круга Кенария многие годы состоят в плодотворном союзе с гиппогрифами. Если конфликт неизбежен, кенарийские боевые гиппогрифы всегда придут на помощь.", 2, "|cFFFFD200Торговец: |rФедриен Быстрое Копье|n|cFFFFD200Зона: |rЗангартопь (Запределье)|n|cFFFFD200Фракция: |rКенарийская экспедиция – превознесение|n|cFFFFD200Цена: |r2000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [44151] = { 24654, 1, 128, "Турбоветролет", "Только самые талантливые и смелые инженеры способны создать летательный аппарат, который может похвастаться стабильностью и безопасностью. Но турбоветролеты просто безумны!", 3, "|cFFFFD200Профессия: |rинженерное дело" },
