    [48778] = { 28302, 2, 0, "Конь смерти Акеруса", "Некоторые лошади просто принимают сторону Тьмы. Этот конь родился во Тьме и пропитан ею насквозь.", 2, "|cFFFFD200Класс: |rрыцарь смерти|n" },
