    [58615] = { 31124, 1, 192, "Жестокий дракон Пустоты", "Самые агрессивные из драконов Пустоты стали наградой приверженцам гладиаторской арены, проявившим на ней значительное мастерство.", 2, "|cFFFFD200Старинное|r" },
