    [59568] = { 31695, 1, 128, "Синий дракон", "Не стой между драконом и яростью его.", 0, "|cFFFFD200Источник:|r дар Алекстразы|n|cFFFFD200Зона:|r Око Вечности" },
    [59569] = { 31717, 1, 128, "Бронзовый дракон", "\"Потерянное время никогда не вернуть. Ой! Вот оно, нашла...\" – Хронорму.", 0, "|cFFFFD200Источник: |rосквернитель из рода Бесконечности|n|cFFFFD200Зона: |rочищение Стратхольма" },
    [59570] = { 31697, 1, 128, "Красный дракон", "Все, о чем поют менестрели – глупые выдумки. Дракон и мухи не обидит, только если на него не напасть первым.", 2, "|cFFFFD200Торговец: |rНебостраза|n|cFFFFD200Зона: |rДраконий Погост|n|cFFFFD200Фракция: |rДраконий Союз - превознесение|n|cFFFFD200Цена: |r2000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [59571] = { 31698, 1, 128, "Сумеречный дракон", "Но самое важное правило, которое ни в коем случае нельзя забывать, как сильно бы он ни плакал, как бы ни умолял – никогда не кормить его после полуночи.", 0, "|cFFFFD200Источник:|r Сартарион|n|cFFFFD200Зона:|r Обсидиановое святилище" },
    [59572] = { 31699, 2, 64, "Черный северный медведь", "Таунка используют шкуры черных северных медведей в своих ритуалах. Чаще всего на похоронах охотников на черных северных медведей.", 2, "|cFFFFD200Старинное|r" },
    [59573] = { 31700, 2, },
