    [59785] = { 31849, 2, 4, "Черный боевой мамонт", "В дикой природе эти умные звери ведут себя очень спокойно, но будучи оседланными, они устанавливают особую связь с наездником и бесстрашно и преданно рвутся в бой.", 2, "|cFFFFD200Торговец: |rрыцарь Дамерон|n|cFFFFD200Зона: |rозеро Ледяных Оков|n|cFFFFD200Цена: |r300|Hitem:43228|h|TInterface\\ICONS\\INV_Misc_Platnumdisks:0|t|h" },
    [59788] = { 31850, 2, 4, "Черный боевой мамонт", "Твердая поступь и невозмутимое спокойствие в бою – вот что ценится в этих темношкурых мамонтах.", 2, "|cFFFFD200Торговец: |rкаменный страж Мукар|n|cFFFFD200Зона: |rозеро Ледяных Оков|n|cFFFFD200Цена: |r300|Hitem:43228|h|TInterface\\ICONS\\INV_Misc_Platnumdisks:0|t|h" },
    [59791] = { 31851, 2, 4, "Шерстистый мамонт", "Шерстистые мамонты являются прямыми потомками древних прародителей своего вида. Они куда ближе к природе, чем их короткошерстные родственники.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r200|TINTERFACE\\ICONS\\Spell_Holy_ProclaimChampion:0|t" },
    [59793] = { 31852, 2, 4, "Шерстистый мамонт", "Этот дикий вид не разводят для верховой езды. Большинство шерстистых мамонтов попадают в капкан охотников в дикой природе. Их свирепость и хитрость – результат укрощения и тренировок.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r200|TINTERFACE\\ICONS\\Spell_Holy_ProclaimChampion:0|t" },
    [59797] = { 31854, 2, 4, "Ледяной мамонт", "Сыны Ходира верят, что наезднику на ледяном мамонте не страшны обморожение и снежная слепота.", 2, "|cFFFFD200Торговец: |rЛиллехофф|n|cFFFFD200Зона: |rГрозовая гряда|n|cFFFFD200Фракция: |rСыны Ходира - почтение|n|cFFFFD200Цена: |r1000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [59799] = { 31855, 2, 4, "Ледяной мамонт", "Многие ледяные гиганты утверждают, что научили своих любимцев трубить так, чтобы с гор сошла лавина.", 2, "|cFFFFD200Торговец: |rЛиллехофф|n|cFFFFD200Зона: |rГрозовая гряда|n|cFFFFD200Фракция: |rСыны Ходира - почтение|n|cFFFFD200Цена: |r1000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [59802] = { 31857, 2, },
    [59804] = { 31858, 2, },
    [59961] = { 31902, 1, 128, "Красный протодракон", "Врайкулы первыми обнаружили потенциал в этих драконах. Или вторыми... Зависит от точки зрения...", 5, "|cFFFFD200Достижение: |r\"Слава герою\"|n|cFFFFD200Категория: |rПодземелья и рейды" },
    [59976] = { 31912, 1, 192, "Черный протодракон", "Эта порода была выведена специально, чтобы чествовать героев, проявивших исключительную стойкость и силу духа в Нордсколе.", 2, "|cFFFFD200Старинное|r" },
    [59996] = { 32151, 1, 128, "Синий протодракон", "\"Грозовая гряда! Вот тут-то мы и найдем зверя, достаточно сильного, чтобы я мог отправиться на нем в бой\", – Скади Безжалостный.", 0, "|cFFFFD200Источник: |rСкади Безжалостный|n|cFFFFD200Зона: |rвершина Утгард" },
    [60002] = { 32153, 1, 128, "Затерянный во времени протодракон", "Единственная причина существования времени — чтобы все не случалось одновременно.", 0, "|cFFFFD200Источник: |rзатерянный во времени протодракон|n|cFFFFD200Зона: |rГрозовая гряда" },
    [60021] = { 32156, 1, 192, "Чумной протодракон", "Эта порода была выведена специально, чтобы чествовать героев, проявивших исключительную доблесть и упорство в Нордсколе.", 5, "|cFFFFD200Старинное|r" },
    [60024] = { 32157, 1, 128, "Аметистовый протодракон", "\"Я никак не могу перестать думать о том, что случилось бы, если бы титаны вывели еще один драконий род\", – Алекстраза.", 5, "|cFFFFD200Достижение: |r\"Долгое и странное путешествие\"|n|cFFFFD200Категория: |rИгровые события" },
    [60025] = { 32158, 1, 128, "Дракон-альбинос", "\"Даже тесты ассоциации по контролю распространения драконов не помогли определить, принадлежит ли этот дракон-альбинос к какому-либо из известных драконьих родов\", – Бреанни.", 5, "|cFFFFD200Достижение: |r\"Командир кавалерии\"|n|cFFFFD200Категория: |rОбщие" },
    [60114] = { 32206, 2, 4, "Бронированный бурый медведь", "Стремительность этого медведя усилена его тяжелой броней, зачарованной магами Кирин-Тора.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r750|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [60116] = { 32207, 2, 4, "Бронированный бурый медведь", "Клич \"За Орду!\" звучит как нельзя более устрашающе со спины этого бронированного монстра, на всех парах раскатывающего Альянс по земле.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r750|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [60118] = { 32203, 2, 4, "Черный боевой медведь", "Убить лидеров Орды – уже достижение, а этот страшный зверь станет еще и отличным трофеем.", 5, "|cFFFFD200Достижение: |r\"За Альянс!\"|n|cFFFFD200Категория: |rPvP" },
    [60119] = { 32205, 2, 4, "Черный боевой медведь", "Этого грозного зверя можно получить, окропив свое оружие кровью командиров Альянса. Но убивать их безвозмездно не менее весело.", 5, "|cFFFFD200Достижение: |r\"За Орду!\"|n|cFFFFD200Категория: |rPvP" },
    [60120] = { 32208, 1, },
    [60136] = { 32212, 0, 64, "Большой караванный мамонт", "Продавцы, путешествующие по самым опасным торговым маршрутам Азерота, готовы доверить свою безопасность только этому огромному мамонту.", 2, "|cFFFFD200Старинное|r" },
    [60140] = { 32213, 0, 64, "Большой караванный мамонт", "Встретить этого мамонта на торговых маршрутах Азерота – самое обычное дело. Этот спокойный рослый зверь стал любимцем среди торговцев, путешествующих к экзотическим и удаленным местам торговли.", 2, "|cFFFFD200Старинное|r" },
    [60424] = { 32286, 2, 4, "Анжинерский чоппер", "Это чудо техники – один из немногих в Азероте мотоциклов, рассчитанных на двух седоков.", 3, "|cFFFFD200Профессия: |rинженерное дело" },
    [61229] = { 32335, 1, 132, "Бронированный белоснежный грифон", "\"Когда эти грифоны атакуют, кровь почти не оставляет пятен у них на перьях. Здорово, а?\" – Мей Франкис.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r2000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61230] = { 32336, 1, 132, "Бронированный синий ветрокрыл", "\"Нордскол для ветрокрылов не родная земля, но они удивительно хорошо приспособились к холоду\", – Мей Франкис.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r2000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61289] = { 23966, 0, },
    [61294] = { 32562, 1, 128, "Зеленый протодракон", "Врайкулы первыми обнаружили потенциал в этих драконах. Или вторыми... Зависит от точки зрения...", 0, "|cFFFFD200Источник:|r треснутое яйцо" },
    [61309] = { 33030, 1, 1024, "Великолепный ковер-самолет", "При покупке ковра вы получаете крепкую пропитанную ледяную ткань, укрепленную настоящим шелком паука-ледопряда. Также отдельно вы можете приобрести запасную подушку.", 3, "|cFFFFD200Профессия: |rпортняжное дело" },
    [61425] = { 32633, 2, 4, "Тундровый мамонт путешественника", "Древние манускрипты изображают купцов, путешествующих по ледяной тундре верхом на этих огромных зверях. Эти изображения привели ученых к догадке, что мамонты были первыми одомашненными ездовыми животными.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r20000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61442] = { 32634, 1, },
    [61444] = { 32635, 1, },
    [61446] = { 32636, 1, },
    [61447] = { 32640, 2, 4, "Тундровый мамонт путешественника", "В путешествиях через холодную тундру всадники и купцы единодушно доверяют только этим сильным и выносливым мамонтам.", 2, "|cFFFFD200Торговец: |rМей Франкис|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r20000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61451] = { 33029, 1, 1024, "Ковер-самолет", "Прыгайте на летающий ковер, чтобы вознестись над сияющим, бесконечно прекрасным Азеротом.", 3, "|cFFFFD200Профессия: |rпортняжное дело" },
    [61465] = { 31862, 2, 4, "Большой черный боевой мамонт", "Когда-то эти мамонты перевозили тяжело бронированных гигантов, поэтому они без труда увезут на себе несколько наездников размером поменьше.", 0, "|cFFFFD200Источник: |rдобыча с босса|n|cFFFFD200Зона: |rхранилище Аркавона" },
    [61467] = { 31861, 2, 4, "Большой черный боевой мамонт", "Иногда один мамонт из помета рождается гораздо сильнее и послушнее своих сверстников. Таких особей нарекают \"большими\", и они везут на передовую сразу несколько всадников.", 0, "|cFFFFD200Источник: |rдобыча с босса|n|cFFFFD200Зона: |rхранилище Аркавона" },
    [61469] = { 31857, 2, 4, "Большой ледяной мамонт", "На узких тропинках Грозовой гряды эти мамонты чувствуют себя как дома, поэтому ледяные гиганты доверяют им перевозку грузов и сразу нескольких всадников.", 2, "|cFFFFD200Торговец: |rЛиллехофф|n|cFFFFD200Зона: |rГрозовая гряда|n|cFFFFD200Фракция: |rСыны Ходира (превознесение)|n|cFFFFD200Цена: |r10 000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61470] = { 31858, 2, 4, "Большой ледяной мамонт", "Из-за сильных ветров и лавин Сыны Ходира не советуют путешествовать по Грозовой гряде в одиночку.", 2, "|cFFFFD200Торговец: |rЛиллехофф|n|cFFFFD200Зона: |rГрозовая гряда|n|cFFFFD200Фракция: |rСыны Ходира (превознесение)|n|cFFFFD200Цена: |r10 000|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61983] = { 32931, 0, },
    [61996] = { 31239, 1, 132, "Синий дракондор", "Дракондоры являются одними из самых смертоносных хищников среди известных в мире. Они способны обнаружить добычу с огромного расстояния, кинуться на нее и распотрошить когтями и клювом.", 5, "|cFFFFD200Достижение: |r\"Не имей 100 рублей, а имей 100 коней\"|n|cFFFFD200Категория: |rОбщие" },
    [61997] = { 32944, 1, 132, "Красный дракондор", "Дракондоры являются одними из самых смертоносных хищников среди известных в мире. Они способны обнаружить добычу с огромного расстояния, кинуться на нее и распотрошить когтями и клювом.", 5, "|cFFFFD200Достижение: |r\"Не имей 100 рублей, а имей 100 коней\"|n|cFFFFD200Категория: |rОбщие" },
    [62048] = { 25064, 1, 192, "Иллидарский Предвестник Рока", "Раз в году происходит временной раскол и случаются странные вещи. Разведчики иллидари и Предвестник Рока очень удивились, когда поняли, что попали в Танарис.", 0, "|cFFFFD200Источник: |rПредвестник Рока|n|cFFFFD200Зона: |rТанарис" },
    [63232] = { 33297, 2, 0, "Штормградский скакун", "Хоть эта порода и была выведена в Штормграде, она невероятно устойчива к холодному климату Ледяной Короны.", 2, "|cFFFFD200Торговец: |rкапрал Артур Флю|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63635] = { 33299, 2, 0, "Ящер племени Черного Копья", "Драккари были первыми, кто начал разводить этих ящеров, но затем они перешли племени Черного Копья в Нордсколе.", 2, "|cFFFFD200Торговец: |rСамамба|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63636] = { 33408, 2, 0, "Стальгорнский баран", "Знаток истории из клана Громового Молота утверждает, что эта порода была первой одомашнена дворфами, когда те переселились в Дун Морог.", 2, "|cFFFFD200Торговец: |rДеррик Пегобород|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63637] = { 33298, 2, 0, "Дарнасский ночной саблезуб", "Когда саблезуб ночного эльфа погибает, наездник традиционно оставляет один из его клыков себе на память.", 2, "|cFFFFD200Торговец: |rРуок Хищная Хватка|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63638] = { 33301, 2, 0, "Гномреганский механодолгоног", "\"Когда вам кровь из носу нужно где-то оказаться сию секунду\", – Рилли Веретень.", 2, "|cFFFFD200Торговец: |rРилли Веретень|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63639] = { 33416, 2, 0, "Экзодарский элекк", "Экзодарские элекки были освящены О'росом – наару, который отправился с Дренора в Азерот с Веленом и его последователями.", 2, "|cFFFFD200Торговец: |rИризи|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63640] = { 33409, 2, 0, "Оргриммарский волк", "Недолгое время среди орков бытовало выражение \"голодный как оргриммарский волк\". Оно до сих пор иногда в шутку используется эльфами крови.", 2, "|cFFFFD200Торговец: |rФрека Кровавый Топор|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63641] = { 33300, 2, 0, "Кодо из Громового Утеса", "Этот альфа-самец – воплощение могущества кодо, бродящих по равнинам Мулгора.", 2, "|cFFFFD200Торговец: |rДору Громовой Рог|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63642] = { 33418, 2, 0, "Крылобег из Луносвета", "\"И я такой говорю, что мне чего-то захотелось курицы. А потом лежу распластанный на полу, а эта тварюга пытается попробовать на вкус мои глаза\", – Беззл Рвизасов.", 2, "|cFFFFD200Торговец: |rТреллис Утреннее Солнце|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63643] = { 33414, 2, 0, "Боевой конь Отрекшихся", "Когда павших героев обращают в нежить, та же участь постигает и их скакунов.", 2, "|cFFFFD200Торговец: |rЭлиза Киллиан|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [63796] = { 33848, 1, 128, "Голова Мимирона", "Игрушка титанов, чем-то приглянувшаяся древнему богу Йогг-Сарону. На данный момент это самая большая голова механического гнома в Азероте.", 0, "|cFFFFD200Источник:|r Йогг-Сарон|n|cFFFFD200Зона:|r Ульдуар" },
    [63844] = { 33857, 1, 128, "Гиппогриф Серебряного Авангарда", "Гиппогриф Серебряного Авангарда позволит оседлать себя только самым отважным чемпионам, закаленным в бою.", 2, "|cFFFFD200Торговец: |rинтенданты фракций|n|cFFFFD200Зона: |rСеребряный турнир, Ледяная Корона|n|cFFFFD200Цена: |r150|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t|n" },
    [63956] = { 33892, 1, 128, "Протодракон в железной броне", "Ни меч, ни копье не оставят следа: бронею железной покрыты бока.", 5, "|cFFFFD200Достижение: |r\"Слава рейдеру Ульдуара (25 игроков)\"|n|cFFFFD200Категория: |rПодземелья и рейды" },
    [63963] = { 33904, 1, 128, "Ржавый протодракон", "\"Конечно, сверкает он не так ярко, как другие, но находка все равно ценная\", – Бранн Бронзобород.", 5, "|cFFFFD200Достижение: |r\"Слава рейдеру Ульдуара (10 игроков)\"|n|cFFFFD200Категория: |rПодземелья и рейды" },
    [64656] = { 34154, 2, 64, "Синий боевой конь-скелет", "Это редкая особь, так как большинство представителей вида исчезло во времена правления барона Ривендера в Стратхольме.", 2, "|cFFFFD200Старинное|r" },
    [64657] = { 34155, 2, 0, "Белый кодо", "\"Кодо – опора нашего народа. Торгует ли Орда, кочует или сражается – могучие кодо всегда с нами\", – Харб Точеное Копыто.", 2, "|cFFFFD200Торговец: |rХарб Точеное Копыто|n|cFFFFD200Зона: |rМулгор|n|cFFFFD200Цена: |r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [64658] = { 356, 2, 0, "Черный волк", "Вой этого волка разносится на километры вокруг.", 2, "|cFFFFD200Торговец: |rОгунаро Волкопас|n|cFFFFD200Зона: |rОргриммар|n|cFFFFD200Цена: |r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [64659] = { 34156, 2, 4, "Ядошкурый равазавр", "Вы вырастили этого равазавра из чешуйчатой ящерки, Мор'век бы вами гордился. А еще он бы быстро заставил вас убрать то, что ящер наделал в углу.", 1, "|cFFFFD200Задание: |r\"Они так быстро растут\"|n|cFFFFD200Зона: |rкратер Ун'Горо" },
    [64681] = { 32198, 1, },
    [64731] = { 34187, 2, 2192, "Морская черепаха", "Рыбаки часто вылавливают таких молодых черепашек. В воде они чувствуют себя намного увереннее, чем на суше.", 3, "|cFFFFD200Профессия:|r рыбная ловля|n" },
