    [64927] = { 34225, 1, 192, "Ледяной змей смертоносного гладиатора", "Эти воскрешенные вестники разрушения не боятся смерти, они приносят ее.", 2, "|cFFFFD200Старинное|r" },
    [64977] = { 34238, 2, 0, "Черный конь-скелет", "Отрекшиеся верят, что даже скитающиеся вьючные звери страдают, будучи нежитью.", 2, "|cFFFFD200Торговец: |rЗахария Пост|n|cFFFFD200Зона: |rТирисфальские леса|n|cFFFFD200Цена: |r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
