    [65439] = { 34425, 1, 192, "Ледяной змей гневного гладиатора", "Некоторые из самых могущественных драконов Азерота были призваны сквозь завесу между жизнью и смертью, чтобы быть обращенными в нежить и беспрекословно служить новым хозяевам.", 2, "|cFFFFD200Старинное|r" },
    [65637] = { 34551, 2, 0, "Большой красный элекк", "После прибытия Экзодара на Азерот на свет появились доселе невиданные в Награнде красные детеныши элекка. Некоторые скотоводы считают, что такой цвет был вызван тем же излучением, что обрушилось на остров Кровавой Дымки.", 2, "|cFFFFD200Торговец: |rИризи|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65638] = { 34550, 2, 0, "Стремительный лунопард", "Многие ночные эльфы почитают этих могучих зверей, так как их мерцающая шкура символизирует богиню Луны, Элуну.", 2, "|cFFFFD200Торговец: |rРуок Хищная Хватка|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65639] = { 34556, 2, 0, "Стремительный красный крылобег", "\"Кто крашеная? А, птица! Мы совсем немножко добавили красного\", – Треллис Утреннее Солнце.", 2, "|cFFFFD200Торговец: |rТреллис Утреннее Солнце|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65640] = { 34557, 2, 0, "Стремительный мышастый конь", "Ветеран, закаленный в самых тяжелых боях Серебряного Авангарда.", 2, "|cFFFFD200Торговец: |rкапрал Артур Флю|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65641] = { 34558, 2, 4, "Большой золотистый кодо", "Этот альфа-самец – воплощение могущества кодо, бродящих по Южным степям.", 2, "|cFFFFD200Торговец: |rДору Громовой Рог|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65642] = { 34553, 2, 0, "Турбодолгоног", "\"Это не зеркало заднего вида, это чтобы враги видели хоть что-то кроме твоих выхлопных труб\", – Рилли Веретень.", 2, "|cFFFFD200Торговец: |rРилли Веретень|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65643] = { 34554, 2, 0, "Стремительный лиловый баран", "У баранов Барак Торола толстый череп и массивные рога, что помогает им с легкостью таранить отряды бронированных врагов.", 2, "|cFFFFD200Торговец: |rДеррик Пегобород|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65644] = { 34549, 2, 0, "Стремительный лиловый ящер", "Давние союзники троллей, эти охотники джунглей создают особую связь со своим наездником. Они становятся не просто ездовым животным, а другом и защитником.", 2, "|cFFFFD200Торговец: |rСамамба|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65645] = { 34552, 2, 0, "Белый боевой конь-скелет", "Когда павших героев обращают в нежить, та же участь постигает и их скакунов.", 2, "|cFFFFD200Торговец: |rЭлиза Киллиан|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65646] = { 34555, 2, 0, "Стремительный пурпурный волк", "Волки Орды – это союзники, а не прирученные звери.", 2, "|cFFFFD200Торговец: |rФрека Кровавый Топор|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t5|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [65917] = { 34655, 2, 64, "Волшебный петух", "Магия в руках искусника способна творить удивительные и прекрасные вещи, а в руках идиота ее только и хватает, что на огромного петуха.", 8, "|cFFFFD200Коллекционная карточная игра:|r El Pollo Grande" },
    [66087] = { 35147, 1, 132, "Гиппогриф Серебряного Союза", "Гиппогрифы Серебряного Союза истово преданы как своему наезднику, так и всему Альянсу.", 2, "|cFFFFD200Торговец: |rХайрен Певец Преданий|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Фракция: |rСеребряный Союз – превознесение|n|cFFFFD200Цена: |r150|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [66088] = { 35148, 1, 132, "Дракондор Похитителей Солнца", "Похитители Солнца из Даларана седлают только самых быстрых и грозных дракондоров.", 2, "|cFFFFD200Торговец: |rВасарин Красный Рассвет|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Фракция: |rПохитители Солнца – превознесение|n|cFFFFD200Цена: |r150|Hcurrency:241|h|TInterface\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t|h" },
    [66090] = { 33840, 2, 4, "Кель'дорайский скакун", "Этот скакун – прямой потомок лошадей, которые когда-то паслись на берегах Источника Вечности.", 2, "|cFFFFD200Торговец: |rХайрен Певец Преданий|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Фракция: |rСеребряный Союз – превознесение|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [66091] = { 33841, 2, 4, "Крылобег Похитителей Солнца", "\"На поле боя от скакуна мне нужны скорость, грация и послушание. Этот крылобег отвечает всем требованиям\", – Эрессея Певица Рассвета.", 2, "|cFFFFD200Торговец: |rВасарин Красный Рассвет|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Фракция: |rПохитители Солнца – превознесение|n|cFFFFD200Цена: |r100|Hcurrency:241|h|TInterface\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t|h" },
