    [66846] = { 35169, 2, 0, "Коричневый боевой конь-скелет", "Когда павших героев обращают в нежить, та же участь постигает и их скакунов.", 2, "|cFFFFD200Торговец: |rЗахария Пост|n|cFFFFD200Зона: |rТирисфальские леса|n|cFFFFD200Цена: |r10|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [66847] = { 35168, 2, 0, "Полосатый рассветный саблезуб", "В незапамятные времена ночные эльфы верили, что увидеть этого светлого саблезуба – дурное предзнаменование.", 2, "|cFFFFD200Торговец: |rЛеланай|n|cFFFFD200Зона: |rТельдрассил|n|cFFFFD200Цена: |r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [66906] = { 35179, 2, 0, "Скакун Серебряного Авангарда", "Этот скакун позволит оседлать себя только поборнику Света.", 2, "|cFFFFD200Торговец: |rкавалер-дама Эвники Капсалис |n|cFFFFD200Зона: |rЛедяная корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t|n" },
    [66907] = { 35180, 2, },
    [67336] = { 35362, 1, 192, "Ледяной змей неумолимого гладиатора", "Неукротимые при жизни, эти существа были сломлены некромантией и эзотерическими ритуалами, а их воля теперь подвластна желаниям наездника.", 5, "|cFFFFD200Старинное|r" },
    [67466] = { 35445, 2, 0, "Боевой конь Серебряного Авангарда", "Лишь тот, кто станет чемпионом Серебряного Турнира, получит этого скакуна.", 2, "|cFFFFD200Торговец: |rкавалер-дама Эвники Капсалис |n|cFFFFD200Зона: |rЛедяная корона|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [68056] = { 35809, 2, 4, "Стремительный ордынский волк", "Несколько самых быстрых волков Серебряного Авангарда стали наградой ордынцам, проявившим себя на турнире Ледяной Короны.", 0, "|cFFFFD200Источник: |rСундук с наградами Серебряного Авангарда|n|cFFFFD200Зона: |rИспытание великого крестоносца (10 игроков)" },
    [68057] = { 35808, 2, 4, "Стремительный скакун Альянса", "Несколько самых быстрых волков Серебряного Авангарда стали наградой членам Альянса, проявившим себя на турнире Ледяной Короны.", 0, "|cFFFFD200Источник: |rСундук с наградами Серебряного Авангарда|n|cFFFFD200Зона: |rИспытание великого крестоносца (10 игроков)" },
    [68187] = { 35876, 2, 4, "Белый боевой конь крестоносца", "Эти кони были выбраны для членов Альянса из личных конюшен Тириона Фордринга, чтобы стать наградой самым искусным чемпионам испытания крестоносца.", 5, "|cFFFFD200Достижение: |r\"Дань бессмертию\"|n|cFFFFD200Категория: |rВеликие подвиги" },
    [68188] = { 35878, 2, 4, "Вороной боевой конь крестоносца", "Награда самым умелым чемпионам испытания крестоносца, эти кони были выбраны для ордынцев из личных конюшен Тириона Фордринга.", 5, "|cFFFFD200Достижение: |r\"Дань бессмертию\"|n|cFFFFD200Категория: |rВеликие подвиги" },
