    [69395] = { 36837, 1, 128, "Дракон Ониксии", "\"У нее много общего с матерью, разве что ее не убивали и не возвращали потом к жизни. Но она еще молода\", – Малориак.", 0, "|cFFFFD200Источник: |rОниксия|n|cFFFFD200Зона: |rлогово Ониксии" },
