    [72286] = { 38545, 2, 0, "Непобедимый", "Знаменитый жеребец Артаса Менетила служил принцу и при жизни, и после смерти. Оседлать его – поистине великий подвиг.", 0, "|cFFFFD200Источник: |rКороль-лич|n|cFFFFD200Зона: |rцитадель Ледяной Короны" },
    [72807] = { 38695, 1, 128, "Неуклонный ледяной покоритель", "Выращенные в студеных глубинах Ледяной Короны, ледяные покорители считаются самыми ожесточенными и беспощадными среди всех ледяных змеев.", 5, "|cFFFFD200Достижение: |r\"Слава рейдеру Ледяной Короны (25 игроков)\"|n|cFFFFD200Категория: |rПодземелья и рейды" },
    [72808] = { 38778, 1, 128, "Обагренный ледяной покоритель", "Обагренными ледяными покорителями называют ледяных змеев, чья прежняя жизнь оборвалась особенно ужасно и кроваво.", 5, "|cFFFFD200Достижение: |r\"Слава рейдеру Ледяной Короны (10 игроков)\"|n|cFFFFD200Категория: |rПодземелья и рейды" },
    [73313] = { 39046, 2, 0, "Багровый конь смерти", "Кони в стойлах цитадели Ледяной Короны служат самым приближенным приспешникам Короля-лича.", 1, "|cFFFFD200Задание: |r\"Воссоединение Могрейна\"|n|cFFFFD200Зона: |rцитадель Ледяной Короны" },
