-- Load localization only if it matches the client locale
if GetLocale() ~= "ruRU" then
    return;
end

function ezCollections:GetPetInfo(spellID)
    local info = ezCollections.Pets[spellID];
    if info then
        return unpack(info, 1, 7);
    end
end

ezCollections.Pets =
{
    [4055] = { 2671, 9, 2, nil, "В центре логики механической белки заложены программы сбора как орехов, так и болтов – зимой все пригодится.", 3, "|cFFFFD200Профессия: |rинженерное дело" },
    [10673] = { 7385, 7, 2, nil, "Донни Антания хочет, чтобы после смерти вместе с ней похоронили бомбейскую кошку. Мудрый искатель приключений может использовать ее таланты с гораздо большей пользой.", 2, "|cFFFFD200Торговец: |rДонни Антания|n|cFFFFD200Зона: |rЭлвиннский лес|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10674] = { 7384, 7, 2, nil, "Донни Антания приглашает этих кошек на свои чаепития. Но она не наливает им их любимый напиток: слезы их врагов.", 2, "|cFFFFD200Торговец: |rДонни Антания|n|cFFFFD200Зона: |rЭлвиннский лес|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10675] = { 7383, 7, 2, nil, "В старой альтеракской пословице говорится: \"У кошки девять жизней, но нужна ей только одна\".", 0, "|cFFFFD200Источник: |rслучайная добыча|n|cFFFFD200Зона: |rАльтеракские горы" },
    [10676] = { 7382, 7, 2, nil, "Последний, кто пытался приучить эту кошку к чистоте, выяснил, что лучше грязный коврик, чем изорванное в клочки все остальное.", 2, "|cFFFFD200Торговец: |rДонни Антания|n|cFFFFD200Зона: |rЭлвиннский лес|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10677] = { 7380, 7, 2, nil, "Известна своими голубыми глазами. Также считается деликатесом у гигантских мурлоков.", 2, "|cFFFFD200Торговец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r60|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10678] = { 7381, 7, 2, nil, "Сон – второе любимое занятие этой кошки. Первое – это зевание.", 2, "|cFFFFD200Торговец: |rДонни Антания|n|cFFFFD200Зона: |rЭлвиннский лес|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10679] = { 7386, 7, 2, nil, "У королевы было три котенка. Первый принес ей толстую крысу. Второй вкусного кролика. А белый, ее любимый, – голову ее противника.", 2, "|cFFFFD200Торговец: |rМалыш Тимми|n|cFFFFD200Зона: |rШтормград|n|cFFFFD200Цена: |r60|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10680] = { 7390, 2, 2, nil, "Некоторые говорят, что эту умную птичку можно научить говорить, но она недостаточно умна, чтобы держать клюв закрытым.", 2, "|cFFFFD200Торговец: |rНаркк|n|cFFFFD200Зона: |rТернистая долина|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10682] = { 7391, 2, 2, nil, "Тролли джунглей дрессируют этих птиц, чтобы те имитировали сигналы о помощи, и таким образом заманивают в ловушки ни о чем не подозревающих путешественников.", 0, "|cFFFFD200Источник: |rслучайная добыча|n|cFFFFD200Зона: |rТернистая долина" },
    [10683] = { 7387, 2, 2, nil, "Любимый питомец пиратов из братства Справедливости. Эта пестрая птичка пригодится для запоминания паролей, списков покупок и рецептов крекеров.", 0, "|cFFFFD200Источник: |rпират из братства Справедливости|n|cFFFFD200Зона: |rМертвые копи" },
    [10684] = { 7389, 2, 2, nil, "Эта цветастая птичка является любимым питомцем гоблинов Пиратской Бухты. Она знаменита своей способностью считать монеты, рассчитывать бюджеты и врать по поводу контрактов.", 2, "|cFFFFD200Торговец: |rНаркк|n|cFFFFD200Зона: |rТернистая долина|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Торговец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10685] = { 7394, 2, 2, nil, "Ей тоже кажется, что вы на вкус как курица.", 2, "|cFFFFD200Торговец: |rВолхв Тирт|n|cFFFFD200Зона: |rТысяча Игл|n|cFFFFD200Цена: |r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [10688] = { 7395, 4, 2, nil, "Таракана обычно покупают спонтанно. Но его владельцам очень сложно с ним расстаться... что бы они ни делали.", 2, "|cFFFFD200Продавец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Продавец: |rИеремия Пейсон|n|cFFFFD200Зона: |rПодгород|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10695] = { 7543, 1, 2, nil, "Надеется, что когда-нибудь вырастет и научится уничтожать целые деревни, как мамочка.", 0, "|cFFFFD200Источник: |rДракончики|n|cFFFFD200Зона: |rПылевые топи, Бесплодные земли" },
    [10696] = { 7547, 1, 2, nil, "Как и другие члены семейства синих драконов, эти капризные маленькие животные демонстрируют склонность к тайной магии.", 0, "|cFFFFD200Источник: |rслучайная добыча|n|cFFFFD200Зона: |rАзшара" },
    [10697] = { 7544, 1, 2, nil, "Этот юный дракон родом из Болотины пока еще учится охотиться, дышать огнем и устраивать милые вспышки дикой ярости.", 0, "|cFFFFD200Источник: |rслучайная добыча|n|cFFFFD200Зона: |rБолотина" },
    [10698] = { 7545, 1, 2, nil, "Представители этого уникального рода, берущего свое начало от зеленого дракона Итара, который вырастил и воспитал их, обосновались в джунглях Фераласа.", 0, "|cFFFFD200Источник: |rЮный дракончик, Мечтательный дракончик|n|cFFFFD200Зона: |rБолото Печали" },
    [10703] = { 7550, 8, 2, nil, "Торговцы на ярмарке Новолуния предлагают странные и экзотические чудеса. Также они продают лесных лягушек.", 2, "|cFFFFD200Торговец: |rФлик|n|cFFFFD200Событие:|r ярмарка Новолуния|n|cFFFFD200Цена: |r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [10704] = { 7549, 8, 2, nil, "Известны своими мощными лапами и острым зрением.", 2, "|cFFFFD200Торговец: |rФлик|n|cFFFFD200Событие:|r ярмарка Новолуния|n|cFFFFD200Цена: |r1|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [10706] = { 7555, 2, 2, nil, "Эту хищную птицу с темным оперением друиды Тельдрассила часто считают дурным предзнаменованием.", 2, "|cFFFFD200Торговец: |rШиленаи|n|cFFFFD200Зона: |rДарнас|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10707] = { 7553, 2, 2, nil, "Детям ночных эльфов на первый день рождения дарят перо рогатой совы. Оно является символом удачи.", 2, "|cFFFFD200Торговец: |rШиленаи|n|cFFFFD200Зона: |rДарнас|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10708] = { 7554, 2, 26, nil, "Фурболги Зимних Ключей высоко ценят ослепительно белые перья этих величественных созданий, которые используются для ритуальных целей.", 11, "" },
    [10709] = { 14421, 4, 2, nil, "Разветвленная система нор защищает луговую собачку от массивных копыт ее соседей.", 2, "|cFFFFD200Торговец: |rХальпа|n|cFFFFD200Зона: |rГромовой Утес|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10711] = { 7560, 4, 2, nil, "Если погонитесь за белым кроликом, то точно упадете.", 2, "|cFFFFD200Торговец: |rЯрлин Янтарлен|n|cFFFFD200Зона: |rДун Морог|n|cFFFFD200Цена: |r20|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10713] = { 7561, 7, 2, nil, "Любимый спутник магов Кирин-Тора. Эта рептилия заставляет задуматься, действительно ли она змея... или что-то совсем другое.", 2, "|cFFFFD200Торговец: |rБреанни|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [10714] = { 7565, 7, 2, nil, "Зан'Тиш бесстрашно ищет, ловит и дрессирует этих благородных змей, чтобы они помогали товарищам в битве.", 2, "|cFFFFD200Торговец: |rЗан'Тиш|n|cFFFFD200Зона: |rОргриммар|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10716] = { 7562, 7, 2, nil, "Бурая змейка родом из территорий, находящихся под властью Орды. Она кажется ничем не примечательной, пока ее жертвы не оказываются слишком близко к ней.", 2, "|cFFFFD200Торговец: |rЗан'Тиш|n|cFFFFD200Зона: |rОргриммар|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [10717] = { 7567, 7, 2, nil, "Багровая змея ценится троллями из племени Черного Копья не только за злобную натуру, но и за непоколебимую преданность.", 2, "|cFFFFD200Торговец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Торговец: |rЗан'Тиш|n|cFFFFD200Зона: |rОргриммар|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [12243] = { 8376, 9, 18, nil, "Не волнуйтесь, боевые механизмы и система наведения у этого экземпляра отключены. Кажется...\" – Карлс Пропеллерз.", 1, "|cFFFFD200Задание:|r \"Собственный КПХ\"|n|cFFFFD200Зона:|rПиратская Бухта |n" },
    [13548] = { 30379, 2, 18, nil, "Не говорите, что эта птица слишком кудахчет над потомством, если не хотите получить клювом в лицо.", 1, "|cFFFFD200Задание:|r \"Куд-кудаааа!\"|n|cFFFFD200Зона: |rЗападный край" },
    [15048] = { 9656, 9, 2, nil, "Возможно, взрывоопасен, определенно очарователен. Держите дальше от открытого огня.", 3, "|cFFFFD200Профессия: |rинженерное дело" },
    [15049] = { 9657, 9, 2, nil, "Этот крошечный робот-миротворец, предназначенный для охраны, оснащен новейшими магическими дезинтеграторами и нейтрализаторами толпы. Не для любителей охраны окружающей среды.", 3, "|cFFFFD200Профессия: |rинженерное дело" },
    [15067] = { 9662, 1, 2, nil, "Эти очаровательные зверьки любят ластиться к своим владельцам после долгого дня, полного жестоких кровавых битв.", 0, "|cFFFFD200Источник: |rслучайная добыча|n|cFFFFD200Зона: |rФералас" },
    [15999] = { 10259, 7, 2050, nil, "Ворги – любимые спутники орков и проявляют невиданную преданность на поле боя.", 1, "|cFFFFD200Задание: |r\"Редкие звери Киблера\"|n|cFFFFD200Зона: |rнижняя часть пика Черной горы" },
    [16450] = { 10598, 7, 2, nil, "Паучок Дымной Паутины появился на свет в теплом климате пика Черной горы, но он может выжить почти в любых условиях.", 1, "|cFFFFD200Задание: |r\"Товар на любителя\"|n|cFFFFD200Зона: |rнижняя часть пика Черной горы" },
    [17707] = { 11325, 7, 82, nil, "Этого детеныша панды размером с кружку пива, несмотря на его нарколепсию, очень любят искатели приключений Азерота.", 7, "|cFFFFD200Промоакция:|r коллекционное издание World of Warcraft" },
    [17708] = { 11326, 5, 82, nil, "\"И небеса содрогнутся, и все существа будут трепетать, и конец света совсем ско... Погоди-ка, а чего это ты такая дылда?\"", 7, "|cFFFFD200Промоакция:|r коллекционное издание World of Warcraft" },
    [17709] = { 11327, 4, 82, nil, "Маленький свирепый выходец из роя зергов, зерглинг не из тех, с кем можно не считаться. Очень опасны в больших количествах.", 7, "|cFFFFD200Промоакция:|r коллекционное издание World of Warcraft" },
    [19772] = { 12419, 9, 2, nil, "Талантливые инженеры создали точную копию древесной лягушки, только в десять раз дороже настоящей.", 3, "|cFFFFD200Профессия: |rинженерное дело" },
    [23811] = { 14878, 8, 18, nil, "Легендарная элевая лягушка считалась вымершей, пока странные сорта хмеля на ярмарке Новолуния не заставили ее выйти из укрытия.", 6, "|cFFFFD200Событие:|r ярмарка Новолуния" },
    [24696] = { 15186, 0, 82, nil, "Появление этого любимого всеми и необычайно необщительного мурлока, несомненно, привлечет множество любопытных.", 7, "|cFFFFD200Промоакция:|r BlizzCon 2005" },
    [24988] = { 15358, 0, 16466, nil, "Веселый нрав и игривость Урчаля способны развеять тоску и развеселить даже самого угрюмого.", 7, "|cFFFFD200Промоакция:|r коллекционное издание Burning Crusade (только для Европы)|n" },
    [25162] = { 15429, 5, 2, nil, "Предупреждение: при контакте с омерзительным слизнюченышем следует надевать перчатки и очки. Не мыть. Оставляет пятна на большинстве доспехов.", 0, "|cFFFFD200Источник:|r случайная добыча|n|cFFFFD200Существа:|r слизнюки и слизни." },
    [26010] = { 15699, 9, 2, nil, "В эту умную игрушку встроен переключатель, выводящий ее из так называемого \"спокойного\" режима. К счастью, даже инженеры, которые строили ее, не знают, где он находится.", 3, "|cFFFFD200Профессия: |rинженерное дело" },
    [26045] = { 15710, 6, 2, nil, "Любит танцевать, махать лапами и играть! Тает после Зимнего Покрова, но не переживайте – когда-нибудь он вернется!", 6, "|cFFFFD200Событие:|r Зимний Покров|n" },
    [26529] = { 15706, 4, 2, nil, "Он бы предпочел не проводить больше времени в праздничной подарочной коробке с ленточкой.", 6, "|cFFFFD200Событие:|r Зимний Покров" },
    [26533] = { 15698, 0, 2, nil, "Эти несезонные союзники без устали работают над подготовкой к Зимнему Покрову и будут благодарны, если их не станут беспокоить в другое время года.", 6, "|cFFFFD200Событие:|r Зимний Покров" },
    [26541] = { 15705, 0, 2, nil, "В ходе таинственного празднования Зимнего Покрова эти верные спутники распространяют по всей земле радость. В остальное время года они заняты.", 6, "|cFFFFD200Событие:|r Зимний Покров" },
    [27241] = { 16069, 0, 16466, nil, "Гурчаль – прирожденная артистка, однако она избегает людей и появляется на публике крайне редко.", 7, "|cFFFFD200Промоакция:|r промоакция европейского фансайта|n" },
    [27570] = { 16085, 0, 2, nil, "Сила любви может быть опасной, особенно когда ей распоряжается летающий гоблин с дурным характером.", 6, "|cFFFFD200Событие:|r Любовная лихорадка|n|cFFFFD200Торговец: |rмилая продавщица|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\INV_ValentinesCard01:0|t|n" },
    [28505] = { 16456, 7, 16466, nil, "Этот детеныш во многом схож со своими родичами, но выделяется среди них исключительными способностями к пению.", 7, "|cFFFFD200Промоакция:|r iCoke" },
    [28738] = { 16547, 8, 2, nil, "Неторопливо и с чувством собственного достоинства побеждает в гонке.", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [28739] = { 16548, 4, 2, nil, "Многие полагают, что имя \"Вертихрюш\" – намек на игривые повадки этой свинки. Но истинное его значение они понимают лишь после того, как почувствуют, что она делает с их внутренностями.", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [28740] = { 16549, 4, 2, nil, "Прирожденный тактик, этот усач притворяется мертвым, чтобы обмануть хищников и сборщиков налогов.", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [28871] = { 16701, 6, 2, nil, "Этот маленький кусочек лета пахнет теплыми вечерами и грозами. Если присмотреться, можно разглядеть лицо.", 6, "|cFFFFD200Событие:|r Огненный солнцеворот|n|cFFFFD200Торговец: |rторговец праздника Огненного солнцеворота|n|cFFFFD200Цена: |r350|TINTERFACE\\ICONS\\INV_SummerFest_FireFlower:0|t" },
    [30156] = { 17255, 2, 2, nil, "После вторжения гарпий в Фералас были атакованы несколько территорий гнездования гиппогрифов. Сотни детенышей остались сиротами и были переданы на попечение друидов острова Сардор.", 8, "|cFFFFD200Коллекционная карточная игра:|r Heroes of Azeroth" },
    [32298] = { 18381, 1, 82, nil, "Этот юный отпрыск драконов Пустоты с Запределья все еще учится быть охотником, защитником и очаровательным ужасом небес.", 7, "|cFFFFD200Промоакция:|r коллекционное издание Burning Crusade" },
    [33050] = { 18839, 8, 2, nil, "Волшебных раков не бывает без волшебных желаний. Если хочешь рыбку ты поймать, потрудись желанье загадать.", 3, "|cFFFFD200Рыбалка:|r рыбалка (430)|n|cFFFFD200Зона:|r лес Тероккар (места для рыбалки)" },
    [35156] = { 20408, 5, 2, nil, "Эфириал Рашаад начал разводить этих загадочных существ после того, как узнал об их способности питаться магической энергией.", 2, "|cFFFFD200Торговец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [35239] = { 20472, 4, 2, nil, "Этот кролик – самое милое создание, которое увидит его оппонент. Он же будет и самым последним созданием.", 2, "|cFFFFD200Торговец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r10|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [35907] = { 21010, 2, 2, nil, "Это волшебное насекомое, нежное создание острова Лазурной Дымки, по слухам, обладает целительной силой.", 2, "|cFFFFD200Торговец: |rСиксс|n|cFFFFD200Зона: |rЭкзодар|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [35909] = { 21009, 2, 2, nil, "Продукт воздействия дикой магической энергии, это насекомое – неожиданно разноцветный результат перемещения азеротской моли в заповедниках Пустоверти.", 2, "|cFFFFD200Торговец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r10|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [35910] = { 21008, 2, 2, nil, "Предпочитает летать во время бури, которые часто бушуют на острове Лазурной Дымки.", 2, "|cFFFFD200Торговец: |rСиксс|n|cFFFFD200Зона: |rЭкзодар|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [35911] = { 21018, 2, 2, nil, "Предпочитает летать в туманные ночи, которые часто бывают на острове Лазурной Дымки.", 2, "|cFFFFD200Торговец: |rСиксс|n|cFFFFD200Зона: |rЭкзодар|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [36027] = { 21055, 1, 2, nil, "Когда-то зводчиков дракондоров в Луносвете высоко ценили эту породу. Хищникам легко заметить его ночью, поэтому он с детства учится осторожности.", 2, "|cFFFFD200Торговец: |rДжиланна|n|cFFFFD200Зона: |rлеса Вечной Песни|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [36028] = { 21064, 1, 2, nil, "Являются смертельными врагами серебряных дракондоров, но состоят в прекрасных отношениях с золотистыми.", 2, "|cFFFFD200Торговец: |rДжиланна|n|cFFFFD200Зона: |rлеса Вечной Песни|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [36029] = { 21063, 1, 2, nil, "Свирепая порода дракондоров. Часто можно наблюдать, как они пикируют с неба на добычу.", 2, "|cFFFFD200Торговец: |rДжиланна|n|cFFFFD200Зона: |rлеса Вечной Песни|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-SILVERICON.BLP:0|t" },
    [36031] = { 21056, 1, 2, nil, "Выращены теми немногими, кто еще желает торговать в Пустоверти.", 2, "|cFFFFD200Торговец: |rделец Рашаад|n|cFFFFD200Зона: |rПустоверть|n|cFFFFD200Цена: |r10|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [36034] = { 21076, 2, 2, nil, "Обычно используется спорлингами как источник света, когда они плывут по водам Зангартопи. Это светящееся насекомое помогает им избежать опасных болотных иглоколов.", 0, "|cFFFFD200Источник: |rболотный иглокол|n|cFFFFD200Зона: |rЗангартопь" },
    [39181] = { 22445, 2, 18, nil, "Всплеск магической энергии, произошедший при превращении Небокрыла обратно в араккоа, освободил это несчастное создание, плененное Луангой, чтобы подпитать проклятие превращения в птицу.", 1, "|cFFFFD200Задание:|r \"Небокрыл\"|n|cFFFFD200Зона:|r лес Тероккар|n|n" },
    [39709] = { 22943, 4, 2, nil, "Множество пьяных посетителей Хмельного фестиваля рассказывают историю об этом загадочном животном, но мало кто помнит эту историю наутро.", 6, "|cFFFFD200Событие:|r Хмельной фестиваль" },
    [40405] = { 23198, 4, 16466, nil, "Везунчик визжит, когда сердится или когда ест. В любом случае не стоит его беспокоить.", 7, "|cFFFFD200Промоакция:|r World Wide Invitational 2007|n" },
    [40549] = { 23234, 7, 2, nil, "Хоть Ландро и не хотел расставаться с Бананчиком, он понял, что для питомца будет безопаснее находиться подальше от Пиратской Бухты, когда его отец, король Мукла, поймет, что он исчез.", 8, "|cFFFFD200Коллекционная карточная игра:|r Through the Dark Portal" },
    [40613] = { 23231, 5, 2, nil, "Он немного злобный, но при этом крайне сонный. Держите его подальше от зверьков!", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [40614] = { 23258, 4, 2, nil, "Син'дорай используют крылобегов в качестве верховых животных, но этот малыш был признан негодным после того, как у него обнаружили множество нервных болезней.", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [40634] = { 23266, 4, 2, nil, "Одомашненные элекки дрессируются дренеями как верховые животные. В дикой природе на них охотятся ради их бивней.", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [40990] = { 23274, 4, 82, nil, "Вонюша своим запахом стремится отпугивать хищников и привлекать кошек. Первое у него получается лучше, чем последнее.", 5, "|cFFFFD200Достижение: |r\"Маленькая такая компания\"|n|cFFFFD200Категория: |rОбщее" },
    [42609] = { 23909, 6, 2, nil, "Эта озорная тыковка любит выскакивать из земли и пугать вас до смерти. Да здравствует тыквенная песня!", 6, "|cFFFFD200Событие:|r Тыквовин" },
    [43697] = { 24388, 8, 2, nil, "Шкуру и мясо кроколиска ценят охотники, но этот зверь очень свиреп и его сложно застать врасплох.", 3, "|cFFFFD200Профессия: |rрыбная ловля|n|cFFFFD200Зона: |rШаттрат" },
    [43698] = { 24389, 8, 2, nil, "Смердых похож на остальных кроколисков, однако он обладает потрясающим чувством юмора.", 3, "|cFFFFD200Профессия: |rрыбная ловля|n|cFFFFD200Зона: |rШаттрат" },
    [43918] = { 24480, 8, 2, nil, "Знахари из племени Амани превратили многих жертв в лягушек. Некоторые так никогда и не смогут вернуть свой прежний облик.", 0, "|cFFFFD200Источник:|r дубравная лягушка|n|cFFFFD200Зона:|r Зул'Аман" },
    [44369] = { 24753, 4, 2, nil, "Робкое создание, розовый элекк дает знать о своем присутствии только тем, кто с ним подружился, или кому-нибудь слишком пьяному, чтобы причинить ему вред.", 6, "|cFFFFD200Событие:|r Хмельной фестиваль|n|cFFFFD200Торговец: |rБелби Быстросмен|n|cFFFFD200Зона: |rДун Морог|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\INV_Misc_Coin_01:0|t|n|n|cFFFFD200Торговец: |rБликс Чиништукс|n|cFFFFD200Зона: |rДуротар|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\INV_Misc_Coin_01:0|t" },
    [45082] = { 25062, 2, 2, nil, "Дикие спороскаты – свирепые животные, но, если их приручить в юном возрасте, из них получатся верные спутники.", 2, "|cFFFFD200Торговец: |rМикаа|n|cFFFFD200Зона: |rЗангартопь|n|cFFFFD200Фракция: |rСпореггар – превознесение|n|cFFFFD200Цена: |r30|TINTERFACE\\ICONS\\INV_Mushroom_02:0|t" },
    [45125] = { 25109, 9, 2, nil, "Когда Пылающий Легион вторгся в Запределье, Ландро Дальнострел вложил свою лепту в военные усилия, создав этот образец. И Орда, и Альянс продолжили производство.", 8, "|cFFFFD200Коллекционная карточная игра:|r March of the Legion" },
    [45127] = { 25110, 2, 2050, nil, "Намного тише настоящего дракона.", 8, "|cFFFFD200Коллекционная карточная игра:|r March of the Legion" },
    [45174] = { 25146, 4, 82, nil, "Этот золотой поросенок почитает предков, принося удачу союзникам.", 7, "|cFFFFD200Промоакция:|r пригласи друга|n" },
    [45175] = { 25147, 4, 82, nil, "Друиды полагают, что у серебряных поросят есть особая связь с золотыми поросятами, и обе породы появились в Азероте в одно и то же время.", 7, "|cFFFFD200Промоакция:|r пригласи друга" },
    [45890] = { 25706, 6, 2, nil, "Шаман-старейшина предупредил, что огоньки Рагнароса, верные слуги Повелителя Огня, появились на Огненной передовой. Нужно быть бдительными.", 11, "" },
    [46425] = { 26050, 8, 2, nil, "Кроколиски – шестилапые амфибии-хищники, обитающие в прибрежных водах. Археологи полагают, что они могут происходить от деметродонов кратера Ун'Горо.", 3, "|cFFFFD200Профессия: |rрыбная ловля|n|cFFFFD200Зона: |rШаттрат" },
    [46426] = { 26056, 8, 2, nil, "После изучения в течение нескольких лет особей кроколисков, знаменитый рыбак Нат Пэгл уверился в том, что их детеныши как правило безобидны.", 3, "|cFFFFD200Профессия: |rрыбная ловля|n|cFFFFD200Зона: |rШаттрат" },
    [46599] = { 26119, 6, 2, nil, "Детеныш бога-феникса Ал'ара. Птенец взлетает сразу после рождения.", 0, "|cFFFFD200Источник: |rКель'тас Солнечный Скиталец|n|cFFFFD200Зона: |rтерраса Магистров" },
    [48406] = { 27217, 1, 16402, nil, "Эти потрясающие существа, которыми награждают героев за победу в битве, олицетворяют храбрость и силу.", 7, "|cFFFFD200Промоакция:|r участие в боях на полях боя" },
    [48408] = { 27346, 1, 16402, nil, "Только герои, доказавшие свою доблесть великими делами, получают в награду этих загадочных змееподобных существ.", 7, "|cFFFFD200Промоакция:|r PvP-бои во время олимпиады в Пекине" },
    [49964] = { 27914, 5, 2, nil, "Эфириальные продавцы душ отправятся в путешествие с теми, кто может освободить нужные им сущности от непослушных хозяев.", 8, "|cFFFFD200Коллекционная карточная игра:|r The Hunt for Illidan" },
    [51716] = { 28470, 2, 2, nil, "Скаты пустоты родом из леса Тероккар. Они мечут икру в период дождей; из сотен мальков, появляющихся из одной кладки, лишь немногие доживают до зрелого возраста.", 2, "|cFFFFD200Торговец: |rГрелла|n|cFFFFD200Зона: |rлес Тероккар|n|cFFFFD200Фракция: |rстражи небес Ша'тар – превознесение|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [51851] = { 28513, 3, 16402, nil, "Отрекшиеся Подгорода и аманийские тролли Зул'Амана дрессируют летучих мышей – вампиров как разведчиков и верховых животных.", 0, "|cFFFFD200Источник: |rпринц Тенрис Мутная Кровь|n|cFFFFD200Зона: |rКаражан" },
    [52615] = { 28883, 3, 82, nil, "Был случайно создан, когда смертоносная сила Короля-лича возродила Инея из мертвых.", 7, "|cFFFFD200Промоакция:|r коллекционное издание Wrath of the Lich King" },
    [53082] = { 29089, 0, 16466, nil, "\"Я само Правосудие!\"", 7, "|cFFFFD200Промоакция:|r World Wide Invitational 2008" },
    [53316] = { 29147, 3, 2, nil, "Предчувствуя неминуемую смерть ближайшего существа, череп издает пронзительное хихиканье.", 2, "|cFFFFD200Торговец: |rДарахир|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r40|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [54187] = { 24968, 9, 2, nil, "Этот механический воин – лучший подарок на Зимний Покров. Батарейки в комплект не входят, зато к нему прилагаются ракеты!", 6, "|cFFFFD200Событие:|r Зимний Покров" },
    [55068] = { 29726, 8, 16466, nil, "Внешность мистера Холодка напоминает о более цивилизованной эпохе, когда дуэлянты сражались во фраках.", 7, "|cFFFFD200Промоакция:|r объединение учетной записи WoW и Blizzard" },
    [59250] = { 31575, 4, 2, nil, "Вам не захочется встретиться с этой крысой в темной подворотне. И вообще, где бы то ни было.", 3, "|cFFFFD200Профессия: |rрыбная ловля|n|cFFFFD200Зона: |rДаларан" },
    [61348] = { 32589, 2, 2, nil, "В этом яйце, в отличие от многих других \"загадочных яиц\" горлока Джиуна, действительно оказалось живое существо.", 0, "|cFFFFD200Источник: |rзагадочное яйцо" },
    [61349] = { 32590, 2, 2, nil, "Редкий скворец-альбинос священен для оракулов. Они считают его вестником проливных дождей, хороших урожаев червей и спячки волчеров.", 0, "|cFFFFD200Источник: |rзагадочное яйцо" },
    [61350] = { 32592, 1, 2, nil, "Эти прожорливые дракончики невероятно быстро растут и могут сожрать за день в два раза больше сырого мяса, чем весят сами.", 0, "|cFFFFD200Источник: |rзагадочное яйцо" },
    [61351] = { 32591, 7, 2, nil, "Кобры Азерота знамениты своим быстродействующим ядом и молниеносными атаками.", 0, "|cFFFFD200Источник: |rзагадочное яйцо" },
    [61357] = { 32595, 8, 2, nil, "Обладает голубыми глазами и любит лихо скользить.", 2, "|cFFFFD200Продавец: |rСайрук|n|cFFFFD200Зона: |rДраконий Погост|n|cFFFFD200Фракция: |rКалу'ак – превознесение|n|cFFFFD200Цена: |r12|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Продавец: |rТанайка|n|cFFFFD200Зона: |rРевущий фьорд|n|cFFFFD200Фракция: |rКалу'ак – превознесение|n|cFFFFD200Цена: |r12|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [61472] = { 32643, 6, 82, nil, "Чтобы не позволить ученикам тайно использовать фамилиара для исполнения их обязанностей, верховные маги поместили описание ритуала призыва этого существа между страниц книг заклинаний, запрятанных по всему Даларану.", 5, "|cFFFFD200Достижение:|r Ученье – свет!|n|cFFFFD200Категория: |rОбщие" },
    [61725] = { 32791, 4, 2, nil, "Весенние кролики символизируют плодовитость и любят демонстрировать почему.", 6, "|cFFFFD200Событие:|r Сад чудес|n|cFFFFD200Торговец: |rторговец Сада чудес|n|cFFFFD200Цена: |r100|TINTERFACE\\ICONS\\Achievement_Noblegarden_Chocolate_Egg:0|t" },
    [61773] = { 32818, 2, 82, nil, "Он скромный, проницательный... и отлично сочетается по вкусу с клюквенным соусом.", 6, "|cFFFFD200Событие:|r Пиршество странников" },
    [61855] = { 32841, 7, 16466, nil, "Этих редких медведей разводят, поскольку они небольшого размера и всегда молодо выглядят.", 5, "|cFFFFD200Достижение: |r\"4-я годовщина WoW\"|n|cFFFFD200Категория: |rВеликие подвиги" },
    [61991] = { 32939, 4, 82, nil, "Она любит компанию друзей. Если точнее, то семидесяти пяти.", 5, "|cFFFFD200Достижение: |r\"Охотник на мелкую дичь\"|n|cFFFFD200Категория: |rОбщее" },
    [62491] = { 33188, 6, 2, nil, "Всегда просыпается, танцуя. Какое счастливое деревце!", 2, "|cFFFFD200Торговец: |rРуок Хищная Хватка|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [62508] = { 33194, 7, 2, nil, "Медведи Дун Морога славятся своей выносливостью, преданностью и боевыми качествами. Время, проведенное с дворфами, не слишком обуздало их свирепость.", 2, "|cFFFFD200Торговец: |rДеррик Пегобород|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [62510] = { 33197, 2, 2, nil, "Может чувствовать биение сердца оппонента через эхолокацию.", 2, "|cFFFFD200Торговец: |rЭлиза Киллиан|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [62513] = { 33198, 7, 2, nil, "Взрывоопасный дуротарский скорпион – яростный хищник пустыни. Известен тем, что только иногда жалит своего хозяина.", 2, "|cFFFFD200Торговец: |rФрека Кровавый Топор|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [62516] = { 33200, 4, 2, nil, "В отличие от тех, кто был лишь превращен в овцу, настоящая овца может быть весьма устрашающей.", 2, "|cFFFFD200Торговец: |rкапрал Артур Флю|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [62542] = { 33219, 4, 2, nil, "На открытом пространстве долгонога догнать невозможно, поэтому хищники, нападая на них, рассчитывают на хитрость и нападают всей стаей.", 2, "|cFFFFD200Торговец: |rДору Громовой Рог|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [62561] = { 33226, 8, 2, nil, "Прибрежный ползун приспособился к северному холоду, впадая в спячку на пляжах островов.", 3, "|cFFFFD200Профессия: |rрыбная ловля|n|cFFFFD200Зона: |rДаларан" },
    [62562] = { 33205, 6, 2, nil, "Отдаленные последствия загрязнения, причиной которого стал Экзодар, до сих пор находятся под наблюдением.", 2, "|cFFFFD200Торговец: |rИризи|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t |n" },
    [62564] = { 33227, 5, 2, nil, "Созданная помощником эльфа крови, эта метла может быть крайне полезна, если кто-нибудь выяснит, как ее остановить.", 2, "|cFFFFD200Торговец: |rТреллис Утреннее Солнце|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t" },
    [62609] = { 33238, 0, 659, nil, "Множество штормградских юнцов присоединилось к Серебряному Авангарду в качестве оруженосцев, в надежде, что однажды послужат Свету в битве.", 1, "|cFFFFD200Задание:|r \"Рождение героя\"|n|cFFFFD200Зона:|r Ледяная Корона" },
    [62674] = { 33274, 9, 2, nil, "Используемый как тренировочное верховое животное для юных гномов, механобип совсем не такой быстрый, но такой же очаровательный, как его старшие братья.", 2, "|cFFFFD200Торговец: |rРилли Веретень|n|cFFFFD200Зона: |rЛедяная Корона" },
    [62746] = { 33239, 0, 403, nil, "Эти юные орки стремятся овладеть добродетелями Серебряного Авангарда: мужеством, силой, честностью.", 1, "|cFFFFD200Задание:|r \"Рождение героя\"|n|cFFFFD200Зона:|r Ледяная Корона" },
    [63318] = { 33578, 0, 16466, nil, "Мурчалий – самый страшный из всех мурлоков, которые сражаются на арене. И пока что единственный.", 7, "|cFFFFD200Промоакция:|r турнир арены" },
    [63712] = { 33810, 3, 2050, nil, "Это на самом деле три питомца в одном. Вы не узнаете, что получили, пока не призовете его.", 2, "|cFFFFD200Торговец: |rСамамба|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Зона: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t|n" },
    [65358] = { 34364, 7, 2, nil, "Многие насмехались над этой кошкой перед началом битвы. Их последними словами были, как правило: \"Кис-кис, кошечка\".", 2, "|cFFFFD200Торговец: |rБреанни|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [65381] = { 33530, 0, 18, nil, "Прыгучий гурлок – дальний родственник мурлока.", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [65382] = { 33529, 0, 18, nil, "Несмотря на небольшие размеры, волчеры одни из самых агрессивных существ в Нордсколе. Стать объектом их любопытства зачастую бывает весьма неприятно и опасно.", 6, "|cFFFFD200Событие:|r Детская неделя" },
    [65682] = { 34587, 9, 16466, nil, "Боевой робот, заправленный замечательными красными или синими электролитами.", 7, "|cFFFFD200Промоакция:|r промоакция Mountain Dew" },
    [66030] = { 34694, 0, 82, nil, "Немногие мурлоки способны успешно преодолевать барьеры измерений. Еще меньшее количество возвращается с новыми оружием и доспехами, которыми владеет Пострел.", 7, "|cFFFFD200Промоакция:|r BlizzCon 2009" },
    [66096] = { 34724, 5, 2, nil, "Эти крайне редкие особи маназмея ценятся за уникальную расцветку и спокойный нрав.", 2, "|cFFFFD200Торговец: |rХайрен Певец Преданий|n|cFFFFD200Зона: |rЛедяная Корона|n|cFFFFD200Фракция: |rСеребряный Союз – превознесение|n|cFFFFD200Цена: |r40|TINTERFACE\\ICONS\\Ability_Paladin_ArtofWar.blp:0|t|n" },
    [66520] = { 34930, 5, 82, nil, "Уникальный тигр, вырезанный из нефрита, ценится крайне высоко, однако еще более ценен укрощенный живой нефритовой тигр.", 7, "|cFFFFD200Промоакция:|r пригласи друга" },
    [67413] = { 35396, 7, 2, nil, "Редко покидает гнездо, пока не достигнет зрелости. С удовольствием устраивает пакости в соседних гнездах острозуба.", 0, "|cFFFFD200Источник: |rгнездо Дарта|n|cFFFFD200Зона: |rПылевые топи" },
    [67414] = { 35395, 7, 2, nil, "Из-за высокого спроса на эту породу с лиловой кожей поголовье ящеров рядом с пещерами Стенаний резко сократилось.", 0, "|cFFFFD200Источник: |rзагадочный страж, загадочный опустошитель|n|cFFFFD200Зона: |rпещеры Стенаний" },
    [67415] = { 35400, 7, 2, nil, "Этот хваткий ящер был рожден в ледяных пустошах Нордскола. Воспитание в суровых условиях дает ему силы выжить там, где другие бы погибли.", 0, "|cFFFFD200Источник: |rящер Гундрака|n|cFFFFD200Зона: |rЗул'Драк" },
    [67416] = { 35387, 7, 2, nil, "Отпрыск Такка Прыгуна исключительно подвижен и может с легкостью маневрировать в самых сложных условиях.", 0, "|cFFFFD200Источник: |rгнездо Такка|n|cFFFFD200Зона: |rСтепи" },
    [67417] = { 35399, 7, 2, nil, "За этими детенышами охотятся ради их уникального окраса. Несмотря на заботу Бреанни, дикую натуру ящеров укротить невозможно.", 2, "|cFFFFD200Торговец: |rБреанни|n|cFFFFD200Зона: |rДаларан|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [67418] = { 35397, 7, 2, nil, "Эти детеныши крупнее других маленьких ящеров. Они вырастают в огромных рептилий, способных на чудовищные разрушения.", 0, "|cFFFFD200Источник: |rгнездо равазавра-матриарха|n|cFFFFD200Зона: |rкратер Ун'Горо." },
    [67419] = { 35398, 7, 2, nil, "Матери острозубов до последнего вздоха защищают свои яйца. Несмотря на свое очарование, они крайне жестоки.", 0, "|cFFFFD200Источник: |rгнездо острозуба-матриарха|n|cFFFFD200Зона: |rБолотина" },
    [67420] = { 35394, 7, 2, nil, "Ящеры Раззаши являются одними из самых умных хищников в джунглях, и за это их очень любят тролли из племени Гурубаши.", 0, "|cFFFFD200Источник: |rслучайная добыча|n|cFFFFD200Зона: |rТернистая долина" },
    [67527] = { 35468, 5, 16466, nil, "Ониксовая пантера – словно тень, скользящая в ночи и исчезающая с рассветом.", 7, "|cFFFFD200Промоакция:|r Korea World Event" },
    [68767] = { 36482, 2, 2, nil, "Воздушные змеи, парящие над побережьем Нордскола, – явный признак того, что клыкарры рядом.", 8, "|cFFFFD200Коллекционная карточная игра:|r Scourgewar" },
    [68810] = { 36511, 5, 2, nil, "Ласковые и любопытные, эти котята, похоже, укрываются в самых необычных местах.", 8, "|cFFFFD200Коллекционная карточная игра:|r Scourgewar" },
    [69002] = { 36607, 1, 16466, nil, "Юное порождение Ониксии, этот молодой дракон решил, что не хочет идти по разрушительным стопам своего деда.", 5, "|cFFFFD200Достижение: |r\"5-я годовщина WoW\"|n|cFFFFD200Категория: |rВеликие подвиги" },
    [69452] = { 36871, 6, 82, nil, "Гончие недр, которые водятся на Огненных Просторах и в Огненных Недрах, могут проследить свое происхождение от альфа-самца по имени Магмадар.", 7, "|cFFFFD200Промоакция:|r прикрепление Authenticator|n" },
    [69535] = { 36908, 2, 82, nil, "Подарок от дворфов из клана Громового Молота героям Альянса, эти детеныши – потомки тех самых грифонов, на которых Фалстад и его спутники путешествовали в Грим Батол.", 9, "|cFFFFD200Внутриигровой магазин|r" },
    [69536] = { 36909, 7, 82, nil, "Трог Безног никогда не задумывался над старой оркской поговоркой: \"Не стоит тянуть виверну за хвост\".", 9, "|cFFFFD200Внутриигровой магазин|r" },
    [69539] = { 36910, 5, 82, nil, "Это очень редкое семейство тигров; многие считают (скорее всего, ошибочно), что их уникальный цвет связан с остаточной магией или другим подобным явлением.", 7, "|cFFFFD200Промоакция:|r пригласи друга" },
    [69541] = { 36911, 0, 82, nil, "Пандаренский монах – бесстрашный спутник в путешествии, постоянно оттачивающий свое мастерство... Если только вы не предложите ему выпить. Он сочтет грубым отказаться.", 9, "|cFFFFD200Внутриигровой магазин|r" },
    [69677] = { 36979, 3, 82, nil, "При жизни Кел'Тузад предал Кирин-Тор и создал культ Проклятых. После смерти он служит Плети в качестве верховного лича Наксрамаса.", 9, "|cFFFFD200Внутриигровой магазин|r" },
    [70613] = { 37865, 4, 82, nil, "Пугели, хоть и маленькие, могут быть опасными, если их раздразнить.", 5, "|cFFFFD200Достижение: |r\"Ищу всех!\"|n|cFFFFD200Категория: |rПодземелья и рейды" },
    [71840] = { 38374, 5, 2, nil, "Этот... Хм... \"Очаровательный\" парнишка любит играть с животными. Просто не подпускайте их к нему слишком близко.", 6, "|cFFFFD200Событие:|r Любовная лихорадка" },
    [74932] = { 40198, 6, 2, nil, "Ледяные студики не столь разрушительны, как их изначальный господин, Повелитель Холода Ахун, но славятся тем, что кидают снежки в ничего не подозревающих искателей приключений.", 6, "|cFFFFD200Событие:|r Огненный солнцеворот" },
    [75134] = { 40295, 9, 2, nil, "Самая продающаяся игрушка Джепетто в этом сезоне. У него новые голубые шасси, и все дети Азерота дерутся за этого нарядного боевого робота.", 2, "|cFFFFD200Торговец: |rторговцы в игровых зонах|n|cFFFFD200Цена: |r50|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
    [75613] = { 40624, 1, 82, nil, "Бреанни из Даларана, одна из самых известных в мире коллекционеров экзотических питомцев, наградит загадочным небесным драконом лишь тех, чья страсть к коллекционированию может сравниться с ее увлечением.", 5, "|cFFFFD200Достижение: |r\"Зоомагазинчик\"|n|cFFFFD200Категория: |rБитвы питомцев" },
    [75906] = { 40703, 9, 82, nil, "Сделанное по образцу огромного робота титанов в Ульдуаре, эта взрывоопасное маленькое творение весьма жестоко играет со своими игрушками.", 9, "|cFFFFD200Внутриигровой магазин|r" },
    [75936] = { 40721, 0, 34, nil, "Мурчалий – самый страшный из всех мурлоков, которые сражаются на арене. И вообще единственный.", 11, "" },
    [78381] = { 42078, 9, 82, nil, "Могущественная артиллерия терранской армии. \"Тор\" всегда появляется первым и выбывает из строя последним!", 7, "|cFFFFD200Промоакция:|r коллекционное издание StarCraft II: Wings of Liberty" },
    [23531] = { 14755, 1, 16466, nil, "Легенды гласят, что эти яркие изумрудные существа приносят удачу владельцам и защищают их от напастей во время путешествия.", 7, "|cFFFFD200Промоакция: |r iCoke, Китай" },
    [23530] = { 14756, 1, 16466, nil, "Приносят удачу тем, кто увидит их осенью. Крайне пугливы – прячутся при виде любых незнакомых существ.", 7, "|cFFFFD200Промоакция: |r iCoke, Китай" },
    [28487] = { 16445, 0, 82, nil, "Бурчаль очень застенчива и очаровательна. Ей больше нравится проводить время в компании старых близких друзей, а не заводить новых.", 0, "|cFFFFD200Источник:|r белое яйцо мурлока" },
    [25018] = { 15361, 0, 16466, nil, "Ворчаль – один из самых редких мурлоков в мире.", 7, "|cFFFFD200Промоакция:|r промоакция в Корее" },
    [66175] = { 34770, 3, 2, nil, "Эти одержимые куклы выглядят как живые благодаря эксцентричным духам умерших.", 6, "|cFFFFD200Событие:|r День мертвых" },
    [10699] = { 7546, 1, 2, nil, "Этот бронзовый дракончик был затерян во времени, пока необычное событие не открыло ему дорогу в наш мир.", 0, "|cFFFFD200Источник:|r Железная Орда|n|cFFFFD200Зона:|r Выжженные земли" },
    [30152] = { 17254, 7, 66, nil, "Один из детенышей Аш'алы, которого вам доверили Ткачи Снов.", 2, "|cFFFFD200Торговец: |rСильвия Олений Рог|n|cFFFFD200Зона: |rВаль'шара|n|cFFFFD200Фракция: |rТкачи Снов – почтение|n|cFFFFD200Цена: |r500|TINTERFACE\\MONEYFRAME\\UI-GOLDICON.BLP:0|t" },
};

for _, pet in ipairs({ 10699, 75613, 66175, 10708, 30152 }) do
    ezCollections.Pets[pet][6] = 11;
